import { Component, OnInit } from '@angular/core';
import { FormControl, FormGroup } from '@angular/forms';
import { CertificadorasService } from 'src/app/certificadoras.service';
import { Certificadora } from 'src/app/Certificadora';

@Component({
  selector: 'app-certificadoras',
  templateUrl: './certificadoras.component.html',
  styleUrls: ['./certificadoras.component.css']
})
export class CertificadorasComponent implements OnInit {
  formulario: any;
  tituloFormulario: string = '';

  constructor(private certificadorasService: CertificadorasService) { }

  ngOnInit(): void {
    this.tituloFormulario = 'Nova Certificadora';
    this.formulario = new FormGroup({
      IdCertificadora: new FormControl(null),
      NomeCertificadora: new FormControl(null),
      CNPJ: new FormControl(''),
      Telefone: new FormControl(''),
      email: new FormControl(''),
      NumeroInmetro: new FormControl(null)
    });
  }

  enviarFormulario(): void {
    const certificadora: Certificadora = this.formulario.value;
    this.certificadorasService.cadastrar(certificadora).subscribe(result => {
      alert('Certificadora cadastrada com sucesso!');
    });
  }
}
<div class="container p-5">
  <div class="row">
    <div class="col-6 border border-light rounded p-5 shadow" *ngIf="formulario">
      <h6>{{ tituloFormulario }}</h6>
      <form [formGroup]="formulario" (ngSubmit)="enviarFormulario()">
        <div class="form-group">
          <label>ID da Certificadora</label>
          <input type="text" class="form-control form-control-sm" formControlName="IdCertificadora" />
        </div>
        <div class="form-group">
          <label>Nome da Certificadora</label>
          <input type="text" class="form-control form-control-sm" formControlName="NomeCertificadora" />
        </div>
        <div class="form-group">
          <label>CNPJ</label>
          <input type="text" class="form-control form-control-sm" formControlName="CNPJ" />
        </div>
        <div class="form-group">
          <label>Telefone</label>
          <input type="text" class="form-control form-control-sm" formControlName="Telefone" />
        </div>
        <div class="form-group">
          <label>Email</label>
          <input type="text" class="form-control form-control-sm" formControlName="email" />
        </div>
        <div class="form-group">
          <label>Número Inmetro</label>
          <input type="text" class="form-control form-control-sm" formControlName="NumeroInmetro" />
        </div>
        <div class="container">
          <div class="row">
            <div>
              <button type="submit" class="btn btn-sm btn-outline-secondary">Salvar</button>
            </div>
            <div class="pl-1">
              <button type="button" class="btn btn-sm btn-light">Voltar</button>
            </div>
          </div>
        </div>
      </form>
    </div>
  </div>
</div>
